import React, { useState } from "react";
import 'antd/dist/antd.css';
import {Link} from 'react-router-dom';
import { Input, Switch} from 'antd';
import {
  Navbar,
  Nav,
  Form,
 
  NavDropdown,
} from "react-bootstrap";
import logo from "../images/icon.png";
import classes from "./Navbar.module.css";
import Subnav from "./subnav";

import PopupExample from "../Alerts/model"



const Navba = (props) => {
 
  const [col,setcol]=useState("black")
  //false
  let inputStyle = {
    color: 'white'
  }
  //true
  let inputStyle2 ={
    color:'black'
  }
  function onChange(checked){
    if (checked){
      props.ColorChanger()
      setcol(inputStyle2.color);
      console.log(col)
    }
    else{
      props.ColorChanger()
      setcol(inputStyle.color);
      console.log(col)
    }
  }
  return (
    
    <Navbar  collapseOnSelect expand="lg" bg="black" variant="dark">
      
     <Navbar.Brand style={{color:`${col}`,marginLeft:"50px"}} className={classes.icon} href="/">
        <img
          alt=""
          src={logo}
          width="30"
          height="30"
          className="d-inline-block align-top"
        />{" "}
        CoinStats
      </Navbar.Brand>
      
      <Switch style={{marginLeft:"180px"}} defaultChecked onChange={onChange}/>
      
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav  className="ml-auto" >
        <Nav.Link style={{color:`${col}`}}  href="/portfoliotracker">portfolio Tracker</Nav.Link>
        <Nav.Link style={{color:`${col}`}} href="/">Live Pricing</Nav.Link>
          <Nav.Link style={{color:`${col}`}} href="/pricing">Pricing</Nav.Link>
          <Nav.Link style={{color:`${col}`}} href="/alert">Alerts</Nav.Link>
         
          <NavDropdown  style={{color: "black"}} bg="black" title={<span style={{color:`${col}`}}><i class="fas fa-ellipsis-h" ></i></span>} id="collasible-nav-dropdown">
          <div style={{width:"25px"}}>
          <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
          </div>
         <div className="main-header-menu-back"><div className="main-header-menu"><div className="guide"><div className="show-mobile user-section"><a className="button-with-link" href="/en/login/"><button type="button" id className="jsx-1426819953 standart-button  primary">Login</button></a></div><div className="section-bar"><nav><a className="primary-nav  show-mobile  " href="/" target="_self" rel="noopener">Live Prices</a><a className="primary-nav  show-mobile active portfolio-tab" href="/portfolio" target="_self" rel="noopener">Portfolio Tracker</a><a className="primary-nav  show-mobile  " target="_self" href="/en/news/">News</a><a className="primary-nav  show-mobile  " target="_self" href="/en/pricing/">Pricing</a><a className="primary-nav  show-mobile  " target="_self" href="/en/alerts/">Alerts</a><a className="primary-nav    " target="_self" href="/en/market-report/">24h Report</a><a className="primary-nav    " target="_self" href="/en/press-kit/">Press Kit</a><a className="primary-nav    " href="https://apidocs.coinstats.app" target="_blank" rel="noopener">API Docs</a><a className="primary-nav    " href="https://blog.coinstats.app" target="_blank" rel="noopener">Blog</a><a className="primary-nav    " target="_self" href="/en/widgets/">Widgets</a><a className="primary-nav    " target="_self" href="/en/news/">News</a></nav></div><div className="select-boxes show-mobile"><div className="jsx-3924517124 select-box"><div className="jsx-3924517124 select-box-inner "><select aria-label="select-fiats" className="jsx-3924517124"><option label="USD" className="jsx-3924517124">USD</option><option label="BTC" className="jsx-3924517124">BTC</option><option label="ETH" className="jsx-3924517124">ETH</option><option label="AMD" className="jsx-3924517124">AMD</option><option label="AUD" className="jsx-3924517124">AUD</option><option label="BGN" className="jsx-3924517124">BGN</option><option label="BRL" className="jsx-3924517124">BRL</option><option label="CAD" className="jsx-3924517124">CAD</option><option label="CHF" className="jsx-3924517124">CHF</option><option label="CNY" className="jsx-3924517124">CNY</option><option label="CZK" className="jsx-3924517124">CZK</option><option label="DKK" className="jsx-3924517124">DKK</option><option label="EUR" className="jsx-3924517124">EUR</option><option label="GBP" className="jsx-3924517124">GBP</option><option label="HKD" className="jsx-3924517124">HKD</option><option label="HRK" className="jsx-3924517124">HRK</option><option label="HUF" className="jsx-3924517124">HUF</option><option label="IDR" className="jsx-3924517124">IDR</option><option label="ILS" className="jsx-3924517124">ILS</option><option label="INR" className="jsx-3924517124">INR</option><option label="ISK" className="jsx-3924517124">ISK</option><option label="JPY" className="jsx-3924517124">JPY</option><option label="KRW" className="jsx-3924517124">KRW</option><option label="MXN" className="jsx-3924517124">MXN</option><option label="MYR" className="jsx-3924517124">MYR</option><option label="NOK" className="jsx-3924517124">NOK</option><option label="NZD" className="jsx-3924517124">NZD</option><option label="PHP" className="jsx-3924517124">PHP</option><option label="PLN" className="jsx-3924517124">PLN</option><option label="RON" className="jsx-3924517124">RON</option><option label="RUB" className="jsx-3924517124">RUB</option><option label="SEK" className="jsx-3924517124">SEK</option><option label="SGD" className="jsx-3924517124">SGD</option><option label="THB" className="jsx-3924517124">THB</option><option label="TRY" className="jsx-3924517124">TRY</option><option label="ZAR" className="jsx-3924517124">ZAR</option></select><span className="jsx-3924517124 icon-dropdown_arrow" /></div></div><div className="jsx-3924517124 select-box"><div className="jsx-3924517124 select-box-inner "><select aria-label="select-lang" className="jsx-3924517124"><option label="English" value="EN" className="jsx-3924517124">English</option><option label="German" value="DE" className="jsx-3924517124">German</option><option label="Korean" value="KO" className="jsx-3924517124">Korean</option><option label="Japanese" value="JA" className="jsx-3924517124">Japanese</option><option label="Chinese" value="ZH" className="jsx-3924517124">Chinese</option><option label="Spanish" value="ES" className="jsx-3924517124">Spanish</option><option label="French" value="FR" className="jsx-3924517124">French</option><option label="Armenian" value="HY" className="jsx-3924517124">Armenian</option><option label="Dutch" value="NL" className="jsx-3924517124">Dutch</option><option label="Russian" value="RU" className="jsx-3924517124">Russian</option><option label="Italian" value="IT" className="jsx-3924517124">Italian</option><option label="Portuguese" value="PT" className="jsx-3924517124">Portuguese</option><option label="Turkish" value="TR" className="jsx-3924517124">Turkish</option></select><span className="jsx-3924517124 icon-dropdown_arrow" /></div></div></div><div className="theme-switcher show-mobile"><span className="secondary-label ">LIGHT MODE</span><label className="toggle-switch false"><input alt type="checkbox" /><span className="switcher-check"><i /></span></label></div><div className="market-download-wrapper show-desktop"><a href="https://itunes.apple.com/us/app/coin-stats-btc-eth-xrp-prices-and-altfolio/id1247849330?mt=8" target="_blank" aria-label="app store" rel="noopener"><img src="data:image/svg+xml;base64," alt="App Store" /></a><a href="https://play.google.com/store/apps/details?id=com.coinstats.crypto.portfolio" target="_blank" aria-label="app store" rel="noopener"><img src="/_next/static/images/google-play-48c0afa0a79fa0d84e52233adbf64983.svg" alt="Google Play" /></a></div></div></div><div className="click-out-closer show-desktop" /></div>

            <NavDropdown.Item href="#action/3.2">
              Another action
            </NavDropdown.Item>
           
          </NavDropdown>
          <div  style={{borderRight:  "2px solid rgba(0, 0, 0, 0.15)" , backgroundColor:`${col}`}}></div>
          <NavDropdown style={{color: "black"}} bg="black" title={<span style={{color:`${col}`}}>Currency</span>} id="collasible-nav-dropdown">
            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.2">
              Another action
            </NavDropdown.Item>
            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item href="#action/3.4">
              Separated link
            </NavDropdown.Item>
          </NavDropdown>
          <NavDropdown  title={<span style={{color: `${col}`}}>Language</span>} id="collasible-nav-dropdown">
            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.2">
              Another action
            </NavDropdown.Item>
            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item href="#action/3.4">
              Separated link
            </NavDropdown.Item>
          </NavDropdown>
          
        </Nav>
        <Form inline>
          <Input placeholder="Search" style={{ borderRadius: "15px"}} suffix={<i class="far fa-search"></i>}/>
        </Form>
        <Nav>
          <Link to="/login"><Nav.Link  style={{color:`${col}`}} href="/login">Login</Nav.Link></Link>
          <Nav.Link className={`${classes.bta} btn`} style={{color:`${col}`,backgroundColor:"orange",padding:" 0.63158rem 1.57895rem" ,borderRadius:"25px"}} eventKey={2} href="/getstarted">
            Get Started
          </Nav.Link>
        </Nav>
      </Navbar.Collapse>
      
    </Navbar>
    
  );
};

export default Navba;
