import React, { useState } from "react";
import NavBar from "./Navbar/Navba";
import "./App.css";
import styled, { ThemeProvider } from "styled-components";
import { lightTheme, darkTheme, GlobalStyles } from "./theme.js";
import Footer from "./Footer/Footer";
import "bootstrap/dist/css/bootstrap.min.css";
import Mainsec from "./MainSec/mainsec";
import Pricing from "./Pricing/pricing";
//import Subnav from "./MainSec/subnav";
import Login from "./Login/Login";
import { Route } from "react-router-dom";
import Getstarted from "./Login/Getstarted";
import Alert from "./Alerts/Alert";
//import PopupExample from "./Alerts/model";
import CreateAccount from "./Login/CreateAccount";
import Portfoliotracker from "./PortfolioTracker/Portfoliotracker";
import Question from "./question";

const StyledApp = styled.div`
  color: ${(props) => props.theme.fontColor};
`;

function App() {
  const [theme, setTheme] = useState("light");
 // toggler button for theme changer 
  const themeToggler = () => {
    theme === "light" ? setTheme("dark") : setTheme("light");
  };

  return (
    <ThemeProvider theme={theme === "light" ? lightTheme : darkTheme}>
      <GlobalStyles />
      <StyledApp>
        <NavBar ColorChanger={() => themeToggler()} />

        <switch>
          <Route exact path="/">
            <Mainsec />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/pricing">
            <Pricing thema={theme} />
          </Route>
          <Route path="/getstarted">
            <Getstarted />
          </Route>
          <Route path="/alert">
            <Alert />
          </Route>
          <Route path="/createaccount">
            <CreateAccount />
          </Route>
          <Route path="/portfoliotracker">
            <Portfoliotracker />
          </Route>
        </switch>

        <Footer />
      </StyledApp>
    </ThemeProvider>
  );
}

export default App;

